package loopexample;

public class BasicLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 1;
		num = num + 2;
		num = num + 3;
		num = num + 4;
		num = num + 5;
		num = num + 6;
		num = num + 7;
		num = num + 8;
		num = num + 9;
		num = num + 10;
		
		System.out.println("1부터 10까지의 합은" + num + "입니다.");
		

	}

}
